        if (!ptr.matcher(user.getEmail()).matches()) {
    		res.setStatus(HttpServletResponse.SC_BAD_REQUEST);
    		res.getWriter().println("Invalid email!");
    		return;
        }
        
        if (user.getRPassword() != user.getPassword()) {
    		res.setStatus(HttpServletResponse.SC_BAD_REQUEST);
    		res.getWriter().println("Passwords don't match!");
    		return;
        }
        
        // If the username already exists send error message, otherwise register with the provided data
        UserDAO userDAO = new UserDAO(connection);
        try {
        	if (userDAO.getIdFromUsername(user.getUsername()) != -2) {
        		res.setStatus(HttpServletResponse.SC_BAD_REQUEST);
        		res.getWriter().println("This username is taken");
        		return;
        	}
			userDAO.register(user.getUsername(), user.getName(), user.getSurname(), user.getEmail(), user.getPassword());
		} catch (SQLException e1) {
			e1.printStackTrace();
    		res.setStatus(HttpServletResponse.SC_INTERNAL_SERVER_ERROR);
    		res.getWriter().println("Internal error while creating the username");
			return;
		}
        
        System.out.println("Wow, successful registration!");
        
        // Sending the confirmation message
		res.setStatus(HttpServletResponse.SC_OK);
		res.getWriter().println("Successful registration!\nYou can now use the Login form!");
